  public static TreeNode createSearchTree(int[] a) {
      if (a.length > 0) {
          TreeNode root = new TreeNode(a[0]);
          for (int i = 1; i < a.length; i++) {
              TreeNode c = root;
              TreeNode p = c;
              TreeNode q = new TreeNode(a[i]);
              while (c != null) {
                  p = c;
                  if (a[i] <= c.val) {
                      c = c.left;
                  } else {
                      c = c.right;
                  }
              }
              if (q.val < p.val) {
                  p.left = q;
              } else {
                  p.right = q;
              }
          }
          return root;
      }
      return null;
  }
