```java
public class SemaphoreStudy {

  // 
  public static final int N = 100;
  // 
  public static final int M = 1000;
  // 
  private static final AtomicInteger F = new AtomicInteger();
  // 
  private static final AtomicInteger S = new AtomicInteger();
  // 
  private static Semaphore store = new Semaphore(N);

  public static void main(String[] args) throws BrokenBarrierException, InterruptedException {
    // 1000
    CyclicBarrier BARRIER = new CyclicBarrier(M + 1);
    // 
    ExecutorService pool = Executors.newCachedThreadPool();

    for (int i = 0; i < M; i++) {
      pool.execute(
          () -> {
            try {
              BARRIER.await();
            } catch (Exception e) {
              e.printStackTrace();
            }
            getData();
          });
    }
    System.out.println("21000");
    Thread.sleep(2000);
    //  
    BARRIER.await();
    pool.shutdown();
  }

  /**  */
  public static void getData() {
    while (!store.tryAcquire()) {
      int a = 5000 + new Random().nextInt(1000);
      System.out.println(": " + a + "" + F.incrementAndGet());
      try {
        Thread.sleep(a);
      } catch (InterruptedException e) {
        e.printStackTrace();
      }
    }

    System.out.println("");
    try {
      Thread.sleep(5000);
    } catch (InterruptedException e) {
      e.printStackTrace();
    }
    store.release();
    System.out.println("" + S.incrementAndGet());
  }
}

```

