 public int maxIndexStrLength(String s) {
   int maxLength = 0;
   for (int index = 0; index < s.length(); index++) {

       int length = 1;
       int i = index + 1;
       for (; ; ) {
           if (i >= s.length()) {
               break;
           }
           String ts = s.substring(index, i);
           char c = s.charAt(i);
           if (ts.contains(c + "")) {
